mapper.select:
SELECT htl.hostip ahtp,c.acsdeip hcdp,htl.agentvsn hatn,c.vxstatus avts,c.cpu acpu,c.mem amem,c.syscpu asyscpu,c.sysmem asysmem,c.autoctl atcl FROM hostdtl htl INNER JOIN (SELECT a.* FROM realtime a, (SELECT ahostip,MAX(checktime) checktime FROM realtime GROUP BY ahostip) b WHERE a.checktime = b.checktime AND a.ahostip = b.ahostip) c on htl.hostip = c.ahostip WHERE htl.hostip in
#{hostip}
OR c.acsdeip in
#{hostip}
